import React, { useState, useEffect } from "react";
import axios from "axios";

export default () => {
  const [posts, setPosts] = useState({}); //objectt b/c in posts microservices

  const fetchPosts = async () => {
    const res = await axios.get("http://localhost:4000/posts");

    setPosts(res.data);
  };

  useEffect(() => {
    fetchPosts();
  }, []); // tells react to run this only once

  const renderedPosts = Object.values(posts).map((post) => {
    return (
      <div
        className="card"
        style={{ width: "30%", marginBottom: "20px" }}
        key={post.id}
      ></div>
    );
  }); //gives us array of all objects in 'posts'

  return <div />;
};
